  //     }
  // },
  // "retina_detect": false,
  // interactivity: {
  //     events: {
  //         onhover: {
  //             enable: true,
  //             mode: "bubble"
  //         }
  //     },
  //     modes: {
  //         bubble: {
  //             size: 6,
  //             distance: 40
  //         }
  //     }
  // }

  };

const App = () => {
    return(

            <div className='  center'>
                    <BrowserRouter>
                        <Particles className='particles w-100 vh-100' params={particlesOptions} />
                        <Ham />

                        <Switch>
                            <Route exact path="/" render={props => (<div><About /><JobsCard {...props} jobsList={jobsList}/> <CardList {...props} projectList={projectList}/></div> )}/>
                            <Route path="/JobsCard" render={props => (<div className=' aspect-ratio aspect-ratio--1x1'><JobsCard {...props} jobsList={jobsList} /></div>)}/>

                           <Route path="/CardList" render={props => (<CardList {...props} projectList={projectList}/>)}/> 
                           <Route path="/AboutMe" render={props => (<div className='aspect-ratio aspect-ratio--1x1'> <About/></div>)}/>
                        </Switch>
                    </BrowserRouter>


            </div>

    )
}

export default App;
