import React from 'react';
import { projectList } from './projectList';
import JobsCard from './JobsCard';
import { jobsList } from './jobsList';
import CardList from './CardList';
import Ham from './hamburger';
import { Route, Switch } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';
import About from './About'
import Particles from 'react-particles-js';
import './App.css'

const particlesOptions = {
  //   particles: {
  //     number: {
  //       value: 100,
  //       density: {
  //         enable: true,
  //         value_area: 1500
  //       }
  //     }
  //   },
  //   interactivity: {
  //     events: {
  //         onhover: {
  //             enable: true,
  //             mode: "repulse"
  //         }
  //     }
  // }
  fps_limit: 28,
  particles: {
      number: {
          value: 200,
          density: {
              enable: false
          }
      },
      line_linked: {
          enable: true,
          distance: 30,
          opacity: 0.4
      },
      move: {
          speed: 1
      },
      opacity: {
          anim: {
              enable: true,
              opacity_min: 0.05,
              speed: 2,
              sync: false
          },
          value: 0.4
      }
  },
  polygon: {
      enable: true,
      scale: 5,
      type: "inline",
      move: {
          radius: 10
      },
      url: "./knight.svg",
      inline: {
          arrangement: "equidistant"
      },
      draw: {
          enable: true,
          stroke: {
              "color": "rgba(255, 255, 255, .2)"
          }
      }
  },
  "retina_detect": false,
  interactivity: {
      events: {
          onhover: {
              enable: true,
              mode: "bubble"
          }
      },
      modes: {
          bubble: {
              size: 6,
              distance: 40
          }
      }
  }

  };

const App = () => {
    return(

            <div className='  center'>
                    <BrowserRouter>
                        <Particles className=' w-100 vh-100' params={particlesOptions} />
                        <Ham />

                        <Switch>
                            <Route exact path="/" render={props => (<div><About /><JobsCard {...props} jobsList={jobsList}/> <CardList {...props} projectList={projectList}/></div> )}/>
                            <Route path="/JobsCard" render={props => (<div className=' aspect-ratio aspect-ratio--1x1'><JobsCard {...props} jobsList={jobsList} /></div>)}/>

                           <Route path="/CardList" render={props => (<CardList {...props} projectList={projectList}/>)}/> 
                           <Route path="/AboutMe" render={props => (<div className='aspect-ratio aspect-ratio--1x1'> <About/></div>)}/>
                        </Switch>
                    </BrowserRouter>


            </div>

    )
}

export default App;
