import React from 'react';
import './style.css';
import Tilt from 'react-tilt';


const Job = (props) => {
    return(


        <div className=' cf w-100 flex flex-wrap justify-center ma3'>
            <div className=" fl w-20-l flex justify-center items-center">
                <Tilt  className='Tilt' style={{ height: 150, width: 150 }}>
                    {/* w-80-m mw5 */}
                    <div > 
                        <a  href={props.website} target="_blank">
                            <div className='Tilt-inner' options={{ max: 55, reverse: true }}> <img className='' src={props.logo}/></div>
                        </a>

                    </div>
                </Tilt>
            </div>
            <div className='pa3 fl w-50-l flex flex-column'>
                <div className='light-blue'>
                    <h1>{props.title}</h1>
                </div>
                <div>
                    <p className='white'>
                        {props.description}
                    </p>
                </div>

            </div>
            <div className='fl w-20-ns'>
                <h1 className=' fr light-blue'>
                    {props.years}
                </h1>

            </div>
        </div>
    );
}

export default Job;
